  <div className="absolute bottom-45 left-30" style={{ textAlign: 'center' }}>
    <img src='logo.png' alt='Not found' style={{ margin: 'auto' }} />
    <div className="text-center text-white">
      <h3>Welcome To </h3>
      <h2>KINGFISHER RESORT</h2>
      <h3>Dendeli</h3>
    </div>
  </div>
</div> */}


          <div className='col-lg-6'>
            <div >
            <h2>Log In</h2>
</div>

  {/* <div>
    <label > Email</label>
    <input type="email"  value={email} onChange={(e)=>setEmail(e.target.value)}  placeholder="" required/>
  </div> */}
  {/* <div className="mt-8">
    <label  > Password</label>
    <input type={showPassword ? 'text': 'password'} value={password} onChange={(e)=>setpassword(e.target.value)} />
  </div> */}
  <div >
    <div >
      <input type="checkbox"value={showPassword} onChange={ChangeText} />
        <label  >Show Password</label>
     </div>
  
     <label ><Link to='/adminresetpass'>Forget Password</Link></label>
  </div>
  <button type="submit" onClick={login} >Log in</button>
<p>Donâ€™t have an accout ? <Link to='/signup'><span >Sign up</span></Link></p>
<p>{status}</p>
                <div width={50} height={40}>
                    {loading && <Loader width={30} height={30} />}
                </div>


          </div>

        </div>

       </div>

    </div>
  )
}

export default Login
