import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { MdOutlineSort } from "react-icons/md";
import { Link, Navigate, useLocation } from 'react-router-dom';
import Loader from './Loader';
import { useNavigate } from 'react-router-dom';


const History = () => {
  const location = useLocation();
  const [startDate , setstartDate]=useState(null)
  const [endDate , setendDate]=useState(null)
  const [loading , setLoading]=useState(null)
  const [selectedvalue, setselectedvalue] = useState('id');
  const [currentpage, setcurrentpage] = useState(0); // Start from page 1
  const [offset, setOffset] = useState(0);
  const [customer, setCustomer] = useState([]);
  const [SearchResults, setSearchResults] = useState([]);
  const [showsearch, setShowSearch] = useState(false);

 let today =new Date()
 let dd = today.getDate()
 let mt =today.getMonth() + 1
 let yr = today.getFullYear()
  let cdate = dd+ "-" + mt + "-" + yr;


  const Navigate = useNavigate()

  const recordsPerPage = 10;
 
const searchCustomers = () => {
    console.log(startDate,endDate,currentpage,recordsPerPage)
    setLoading(true);
    axios
      .get(`http://localhost:8080/bill/findBylocalDateBetween/${startDate}/${endDate}`, {
        params: {
          startDate: startDate,
          endDate: endDate,
        },
      })
      .then((response) => {
        setSearchResults(response.data.data);
        setLoading(false);
        setShowSearch(true);
      })
      .catch((err) => {
        if (err.response && err.response.status === 404) {
          alert('Data not found');
        } else {
          console.log(err);
        }
      })
      .finally(() => {
        setLoading(false);
      });
  };

useEffect(() => {
  getAllCommission();
}, [selectedvalue, currentpage]);

useEffect(() => {
  const delay = 2000; // Set your desired delay time in milliseconds (1 second in this example)

   setTimeout(() => {
    searchCustomers();
  }, delay);

  // Cleanup function to clear the timeout in case the component unmounts or the dependencies change
  
}, [startDate, endDate]);



const prepage = () => {

  setcurrentpage(currentpage - 1);

};

const nextpage = () => {
  if (currentpage < offset) {
      setcurrentpage(currentpage + 1);
  }
};

const getAllCommission = () => {
  setLoading(true);
  axios
